mapper.update:
update point_detail
set userid = #{userid,jdbcType=INTEGER},
  createtime = #{createtime,jdbcType=TIMESTAMP},
  num = #{num,jdbcType=INTEGER},
  `source` = #{source,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, userid, createtime, num, `source`
from point_detail
where id = #{id,jdbcType=INTEGER}
